package com.learning.dataconversion;

public class ImplicitDataConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		   /*Implicit Data conversion is a place where the short range value can fit in the
		    * larger range. for example byte can we converted into int and int can be converted to long whereas the
		    * viceversa is difficult
		    *  */
				
				int a = 100;
				
				System.out.println("The integer var a= " + a);
				
				long b =a;
				System.out.println("The long var b= " + b);

	}

}
